import { Component } from '@angular/core';

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.css']
})
export class TeamsComponent implements on{
  teams: { id: number,name: string }[] = [];
  newTeamName: string = '';

  createTeam() {
    if (this.newTeamName.trim() !== '') {
      const newTeam: { id: number, name: string } = {
        id: this.generateUniqueId(), // You can implement a function to generate unique IDs
        name: this.newTeamName
      };

      this.teams.push(newTeam);
      this.newTeamName = '';
    }
  }

  generateUniqueId(): number {
    // Implement a function to generate unique IDs, such as incrementing a counter or using a library
    // For simplicity, you can use a timestamp-based approach
    return new Date().getTime();
  }




  ngOnInit() {
    console.log(this.teams);
    // Fetch or set the data for teams and assign it to this.teams
    // Example: this.teams = someFunctionToGetTeamsData();
  }

}
<form (submit)="createTeam()">
  <input type="text" name="teamName" [(ngModel)]="newTeamName" placeholder="Enter team name" required>
  <button type="submit">Create Team</button>
</form>

<!-- Display a list of existing teams -->
<ul>
  <li *ngFor="let team of teams">{{ team.name }}  <a [routerLink]="['/teams', 'edit', team.id]">Edit</a></li>
</ul>
