import {Component, Input, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, FormArray, Validators, AbstractControl} from '@angular/forms';
import { ValidatorFn } from '@angular/forms';
import {AuthService} from "../../services/auth.service";
import {DataService} from "../../services/data.service";

@Component({
  selector: 'app-schedule-form-edit',
  templateUrl: './schedule-form-edit.component.html',
  styleUrls: ['./schedule-form-edit.component.css']
})
export class ScheduleFormEditComponent implements OnInit {
  hmm!:any;
  scheduleForm!: FormGroup;
  weekdays: string[] = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  formatTimeRange(timeF: HTMLInputElement,timeT: HTMLInputElement,Course: any): string {
    const timeFrom = timeF.value;
    const timeTo = timeT.value;
    Course.get('time')?.setValue((timeFrom+"-"+timeTo).toString());
    return `${timeFrom} - ${timeTo}`;
  }
  constructor(public authService: AuthService,private dataService: DataService,private formBuilder: FormBuilder) {
    this.createContactForm();
    this.loadSchedule();
  }
  createContactForm() {
    this.scheduleForm = this.formBuilder.group({
      owner: ['', Validators.required],
      scheduleEntries: this.formBuilder.array([this.createScheduleEntryFormGroup()])
    });
    this.scheduleForm.get('owner')?.setValue(this.authService.currentUser.userId);
  }
  createScheduleEntryFormGroup(): FormGroup {
    return this.formBuilder.group({
      dayOfWeek: ['', Validators.required],
      courses: this.formBuilder.array([this.createCourseFormGroup()])
    });
  }
  createCourseFormGroup(): FormGroup {
    return this.formBuilder.group({
      time: ['',[Validators.required,timeRangeValidator()]],
      course: ['',[Validators.required,Validators.minLength(2)]]
    });
  }
  get formArr() {
    return this.formArr2.at(0).get('courses') as FormArray;
  }

  get formArr2() {
    return this.scheduleForm.get('scheduleEntries') as FormArray;
  }

  former(x: AbstractControl) {
    return x.get('courses') as FormArray;
  }
  addRow({obj}: { obj: any }) {
    return this.formBuilder.group({
      time: [obj.time,[Validators.required,timeRangeValidator()]],
      course: [obj.course,[Validators.required,timeRangeValidator()]],
    });
  }
  addCol() {

    return this.formBuilder.group({
      dayOfWeek: ['', Validators.required],
      courses: this.formBuilder.array([this.createCourseFormGroup()])
    });

  }
  addNewRow(t : any) {
    let obj1 = {
      time: '',
      course: '',
    };
    t.get('courses').push(this.addRow({obj: obj1}));


  }
  addNewCol() {
    let obj1 = {
      time: '',
      course: '',
    };
    if(this.formArr2.length <6){
      this.formArr2.push(this.addCol());

    }
    return 0;
  }
  ngOnInit(): void {
  }


  addCourse(): void {
    const coursesArray = this.scheduleForm.get('courses') as FormArray;
    coursesArray.push(this.createCourseFormGroup());
  }

  removeCourse(index: number,t : any): void {
    const coursesArray = t.get('courses') as FormArray;
    coursesArray.removeAt(index);
  }
  removeDay(index: number): void {
    const coursesArray = this.formArr2 as FormArray;
    coursesArray.removeAt(index);
  }
  loadSchedule() {
    this.dataService.getScheduleAmountByUserId(this.authService.currentUser.userId).subscribe(data => {
      console.log(data);
      this.hmm = data;
    });
  }
  submitSchedule(): void {
    if (this.scheduleForm.valid) {

      console.log("ok");
      // Send the form data to your backend API for saving to the database
      // You can use Angular's HttpClient to make the API request
      this.dataService.createSchedule(this.scheduleForm.value).subscribe(()=>
      {
        console.log("created");
      });
    }else{
      console.log("!ok");
    }
  }

  protected readonly FormArray = FormArray;
  protected readonly AbstractControl = AbstractControl;
}
<form style="height: 88vh;min-width: 100vw" [formGroup]="scheduleForm" (ngSubmit)="submitSchedule()">

  <tbody class="row" formArrayName="scheduleEntries">
  <tr class="column" *ngFor="let course of formArr2.controls; let x = index;" [formGroupName]="x">
    <div class="m-1  ">
      <td>
        <label for="dayOfWeek">dayOfWeek:</label>
        <select id="dayOfWeek" formControlName="dayOfWeek">
          <option *ngFor="let weekday of weekdays" [value]="weekday">{{ weekday }}</option>
        </select>
        <button type="button" class="btn btn-light m-2" (click)="removeDay(x)">Remove Day</button>
      </td>
      <td class="each" formArrayName="courses">
        <button type="button"  (click)="addNewRow(course)" class="btn btn-success m-2">add Course</button>
        <div class="each2" id="2222" *ngFor="let cour of former(course).controls ; let i = index;" [formGroupName]="i">
          <label for="cors">Course {{i+1}}:</label>
          <div id="cors" class="doc">
            <label for="times">Time:</label>
            <div id="times">
              <label for="timefrom">From:</label>
              <input type="time" id="timefrom" #timefromInput (input)="formatTimeRange(timefromInput,timetoInput,cour);" min="07:00" max="21:00" required />
              <label for="timeto">To:</label>
              <input type="time" id="timeto" #timetoInput (input)="formatTimeRange(timefromInput,timetoInput,cour); " min="{{timefromInput.value}}" max="21:00" required />


            </div>

            <div>Time Range: <p id="time" >{{ formatTimeRange(timefromInput,timetoInput,cour) }}</p></div>
            <label for="course">Course:</label>
            <input type="text" id="course" formControlName="course" />

            <button type="button" class="btn btn-danger m-md-2" (click)="removeCourse(i,course)">Remove Course</button>



          </div></div>


      </td>


    </div>
  </tr>
  <tr>
    <pre style="color: white">{{hmm |json}}</pre>
  </tr>
  <button type="button" (click)="addNewCol()" class="btn btn-dark buttons">add Day</button>
  <button class="btn btn-light buttons" type="submit">Submit</button>
  </tbody>

</form>
