import {Component, Input, OnInit} from '@angular/core';

@Component({
  selector: 'blog-item-text',
  templateUrl: './blog-item-text.component.html',
  styleUrls: ['./blog-item-text.component.css']
})
export class BlogItemTextComponent  implements OnInit {

  @Input() text?: string;
  @Input() price?: string;
  @Input() id?: number;
  buttonText: string = 'check out';
  isHovered: boolean = false;
  constructor() { }

  ngOnInit(): void {
  }

}
<p>{{ text }} </p>
<p>{{price}}</p>
<a href="#" class="btn btn-primary" [routerLink]="['/blog/detail/' + id]" [class]="isHovered | spin" (mouseenter)="isHovered = true" (mouseleave)="isHovered = false">
  {{buttonText }}
</a>
