import {Component, OnInit} from '@angular/core';
import {AuthService} from "../../services/auth.service";
import {Router} from "@angular/router";

@Component({
  selector: 'navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {

  constructor(public authService: AuthService, public router: Router) {
  }

  ngOnInit(): void {
  }

  signOut() {
    this.authService.logout().subscribe((result: any) => {
      this.router.navigate(['/']);
      return result;
    });
  }

}
<nav class="navbar navbar-expand-lg navbar-dark bg-navyblue">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Blog</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#" [routerLink]="['/']">Home</a>
        </li>
        <li *ngIf="authService.isLoggedIn()" class="nav-item">
          <a class="nav-link" href="#" [routerLink]="['/blog']">Blog</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#" [routerLink]="['/addPost']">Add post</a>
        </li>
      </ul>
      <div class="d-flex align-items-center">
        <custom-dropdown>
          <div class="menu-items">
            <button *ngIf="authService.isLoggedIn()"  (click)="signOut()">Wyloguj</button>
            <button *ngIf="!authService.isLoggedIn()" class="btn btn-success" href="#" [routerLink]="['/login']">
              Login
            </button>
            <button *ngIf="!authService.isLoggedIn()"  href="#" [routerLink]="['/signup']">
              SignUp
            </button>
          </div>
          </custom-dropdown>
    </div>
    </div>
  </div>
</nav>
