import {Component, OnInit} from '@angular/core';
import {AuthService} from '../../services/auth.service';
import {Router} from "@angular/router";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {

  public credentials = {
    name: '',
    email: '',
    password: '',
  };
  isSubmitted = false;

  constructor(private authService: AuthService, public router: Router,private formBuilder: FormBuilder) {
  }

  ngOnInit() {

  }

  create() {
    this.isSubmitted = true;

    if (!this.credentials.email) {
      return;
    }
    this.authService.createOrUpdate(this.credentials).subscribe((result) => {
      return result;
    });
    this.router.navigate(['/']);
  }
}
<section class="signup container mt-xl-5 w-25 text-center bg-black py-5 rounded-5 border-light border-5  border text-white  ">
  <h1 class="entry-title "><span>Create Account</span></h1>
  <hr>
  <form class="form-horizontal d-flex flex-column  gap-4 align-items-center pb-5 text-center" novalidate (ngSubmit)="create()" method="post" name="signup" id="signup">
    <div class="form-group ">
      <label class="control-label col-sm-3 text-white">Email<span class="text-danger">*</span></label>
      <div class="col-md-12 col-sm-10">
        <div class="input-group ">
          <span class="input-group-addon"><i class="glyphicon glyphicon-envelope"></i></span>
          <input type="email" class="form-control rounded-5  slim-input" name="emailid" id="emailid" placeholder="Type email"
                 value="" [(ngModel)]="credentials.email" required>
          <div class="error-message" *ngIf="!emailValid && isSubmitted">
            Email is required.
          </div>
        </div>
      </div>
    </div>
    <div class="form-group ">
      <label class="control-label col-sm-3 text-white">Password<span class="text-danger">*</span></label>
      <div class="col-md-12 col-sm-10">
        <div class="input-group">
           <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="password" class="form-control rounded-5 slim-input" name="password" required  id="password"
                 placeholder="Wpisz hasło (5-15 znaków)" value="" [(ngModel)]="credentials.password">
        </div>
      </div>
    </div>
    <div class="form-group">
      <label class="control-label col-sm-5 text-white">Re-password <span class="text-danger">*</span></label>
      <div class="col-md-12 col-sm-10">
        <div class="input-group">
           <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="password" class="form-control rounded-5 slim-input" name="cpassword" required id="cpassword"
                 placeholder="Re-password" value="">
        </div>
      </div>
    </div>
    <div class="form-group">
      <label class="control-label col-sm-3 text-white">Name <span class="text-danger">*</span></label>
      <div class="col-md-12 col-sm-10">
        <input type="text" class=" form-control rounded-5 slim-input" name="mem_name" id="mem_name" required placeholder="Type name" value=""
               [(ngModel)]="credentials.name">
      </div>
    </div>
    <div class="form-group">
      <div class="col-xs-offset-3 col-xs-10">
        <button  name="Submit" type="submit" class="btn btn-outline-light slim-button">Sign Up</button>
      </div>
    </div>
  </form>
</section>
