import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { DataService } from '../../services/data.service';
import {AuthService} from "../../services/auth.service";

@Component({
  selector: 'Msite',
  templateUrl: './Msite.component.html',
  styleUrls: ['./Msite.component.css']
})

export class MsiteComponent implements OnInit {
  @Input() filterText: string = '';

  // @ts-ignore
  public items$ :any;
  offset: number = 0;
  pageSize: number = 6;

  sortDescending: boolean = true; // Initial sorting order
  selectedFilter: string = 'order';
  isLog: boolean = false;
  user: string = '';

  toggleSortOrder() {
    this.sortDescending = !this.sortDescending;
  }
  constructor(private authService: AuthService,private service: DataService) {
  }

  ngOnInit() {
    this.getAll();
    if(this.authService.isLoggedIn()){
      this.isLog = true;
      this.user = this.authService.currentUser.name;

    }


  }
  nextPage() {
    if (this.offset + this.pageSize < this.items$.length) {
      this.offset += this.pageSize;
    }
  }

  previousPage() {
    if (this.offset - this.pageSize >= 0) {
      this.offset -= this.pageSize;
    }
  }
  getAll(){
    this.service.getAll().subscribe(response => {

      this.items$ = response;
    });
  }

}
<div class="container main-page">
  <div class="row">
    <select *ngIf="isLog == true" class="slim-select"  [(ngModel)]="selectedFilter">
      <option value="order">Price Value</option>
      <option value="myProduct">My offers</option>
    </select>
    <button class="slim-button" (click)="toggleSortOrder()">Toggle Sort Order</button>
    <div *ngIf="selectedFilter === 'order'" class="row">
    <div class="col-md-4" *ngFor="let item of items$ | filter:filterText | priceSort: sortDescending  | slice: offset: offset + pageSize">
      <Msite-item class="item" [class.fade-in]="true" [item]="item" >
      </Msite-item>
    </div>
    </div>
    <div *ngIf="selectedFilter === 'myProduct'" class="row">
      <div class="col-md-4" *ngFor="let item of items$| myItemsFilter:user | slice: offset: offset + pageSize">
        <Msite-item class="item" [class.fade-in]="true" [item]="item" >
        </Msite-item>
      </div>
    </div>
  </div>
  <div class="row justify-content-center mt-4 mb-5">
    <nav>
      <ul class="pagination">
        <li class="page-item" [class.disabled]="offset === 0">
          <a class="page-link" href="#" (click)="previousPage()">Previous</a>
        </li>
        <li class="page-item" [class.disabled]="offset >= items$.length - pageSize">
          <a class="page-link" href="#" (click)="nextPage()">Next</a>
        </li>
      </ul>
    </nav>
  </div>
</div>
