import { Component, OnInit } from '@angular/core';
import {DataService} from "../services/data.service";
import {ActivatedRoute, Router} from '@angular/router';
import {AuthService} from "../services/auth.service";

@Component({
  selector: '-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent implements OnInit {
  public credentials = {
    id: '',
    image: '',
    title: '',
    description: '',
    price: '',
    seller:this.authService.currentUser.name,


  };// Object to hold the form data
  productId: string = ''; // Property to store the product ID

  constructor(private authService: AuthService,private productService: DataService, private route: ActivatedRoute,public router: Router) { }

  ngOnInit(): void {
    // Get the product ID from the route parameters

    let id: string = '';
    this.route.paramMap
      .subscribe((params: any) => {
        id = params.get('id');
        this.productId = id;
      });

    this.productService.getById(id).subscribe((res: any) => {
      this.credentials = res;
    });

  }

  updateProduct(): void {
    this.productService.Update(this.credentials).subscribe((result) => {
      return result;
    });
    this.router.navigate(['/']);
  }
}
<section class="signup container">
  <h1 class="entry-title"><span>Edit Product</span></h1>
  <hr>
  <form class="form-horizontal" method="post" name="signup" id="signup">
    <div class="form-group">
      <label class="control-label col-sm-3">Title<span class="text-danger">*</span></label>
      <div class="col-md-5 col-sm-8">
        <div class="input-group">
          <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="text" class="form-control" name="title" id="title"
                 placeholder="Enter Title (5-25 char)" [(ngModel)]="credentials.title">
        </div>
      </div>
    </div>
    <!-- Add other form fields for image, description, price, etc. -->

    <div class="form-group">
      <div class="col-xs-offset-3 col-xs-10">
        <button (click)="updateProduct()" name="Submit" type="submit" class="btn btn-primary">Update Product</button>
      </div>
    </div>
  </form>
</section>
