import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
  currentYear: number = new Date().getFullYear();
}
<footer class="footer">
  <div class="container p-3 rounded-5" style="background-color: #225378; color: white;">
    <p>&copy; {{ currentYear }} Kacper Dworak</p>
  </div>
</footer>
