import {Component, Input, OnInit} from '@angular/core';

@Component({
  selector: 'blog-item',
  templateUrl: './blog-item.component.html',
  styleUrls: ['./blog-item.component.css']
})
export class BlogItemComponent implements OnInit {

  @Input() image?: string;
  @Input() title?: string;
  @Input() price?: string;
  @Input() description?: string;
  @Input() seller?: string;
  @Input() contactEmail?: string;
  @Input() id?: number;

  ngOnInit(): void {
  }

  protected readonly String = String;
}
<blog-item-image class="card-img-top" style=" height: 15rem;" [image]="image"></blog-item-image>
<blog-item-text class="card-text text-center"  [text]="title" [price]="price" [id]="id"></blog-item-text>
