import {Component, OnInit} from '@angular/core';
import {DataService} from "../../services/data.service";
import {ActivatedRoute} from "@angular/router";
import {AuthService} from "../../services/auth.service";
import { Router } from '@angular/router';

@Component({
  selector: 'blog-item-details',
  templateUrl: './blog-item-details.component.html',
  styleUrls: ['./blog-item-details.component.css']
})
export class BlogItemDetailsComponent  implements OnInit {

  public image: string = '';
  public title: string = '';
  public description: string = '';
  public price: number = 0;
  public seller: string = '';
  public itemID: string = '';


  constructor(private service: DataService,public authService: AuthService, private route: ActivatedRoute) {
  }


  ngOnInit() {
    let id: string = '';
    this.route.paramMap
      .subscribe((params: any) => {
        id = params.get('id');
        this.itemID = id;
      });

    this.service.getById(id).subscribe((res: any) => {
      this.image = res['image'];
      this.title = res['title'];
      this.description = res['description'];
      this.price = res['price'];
      this.seller = res['seller'];
    });

  }

  delPost() {
    if(this.authService.isLoggedIn()) {
      this.service.Delete(this.itemID);
      this.Router.navigate(['/'])
    }
  }
}

<img class="rounded float-start w-50 h-75 p-3 mt-1" [src]="image" >
<div class="rounded float-start w-50 h-75 p-3 mt-1 " style="background-color: #225378;">
  <p class="text-center p-3" style="font-family: Arial, sans-serif; color: #FFFFFF;">
    {{ title }}
  </p>
  <p class="p-3" style="color: #E6E6E6;background-color: #2d62a4; height: 50%; overflow-y: auto; font-size: max(14px, calc(14px + (18 - 14) * ((100vw - 300px) / (1600 - 300)))); text-align: justify;">
    {{ description }}
  </p>
  <div style="display: flex; justify-content: flex-end; align-items: center;">
    <button class="btn btn-primary" disabled style="background-color: #3355AA; color: #FFFFFF;">Buy</button>
    <p class="p-3" style="color: #FFFFFF; font-weight: bold; font-size: 20px; margin-left: 10px;">
      ${{ price }}
    </p>
  </div>
  <div *ngIf="authService.isLoggedIn() && this.seller === authService.currentUser.name">
    <button class="btn btn-danger" (click)="delPost()"  style="background-color: #8d004b; color: #FFFFFF;">Delete</button>
  </div>
</div>
