import { Component , OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';

@Component({
  selector: 'app-schedule-form',
  templateUrl: './schedule-form.component.html',
  styleUrls: ['./schedule-form.component.css']
})
export class ScheduleFormComponent  implements OnInit {

  scheduleForm!: FormGroup;

  constructor(private formBuilder: FormBuilder) {
    this.createContactForm();
  }
  createContactForm() {
    this.scheduleForm = this.formBuilder.group({
      courses: this.formBuilder.array([this.createCourseFormGroup()])
    });
  }

  ngOnInit(): void {
  }

  createCourseFormGroup(): FormGroup {
    return this.formBuilder.group({
      time: ['', Validators.required],
      course: ['', Validators.required],
      // Add more fields as needed for each course
    });
  }

  addCourse(): void {
    const coursesArray = this.scheduleForm.get('courses') as FormArray;
    coursesArray.push(this.createCourseFormGroup());
  }

  removeCourse(index: number): void {
    const coursesArray = this.scheduleForm.get('courses') as FormArray;
    coursesArray.removeAt(index);
  }

  submitSchedule(): void {
    if (this.scheduleForm.valid) {
      // Send the form data to your backend API for saving to the database
      // You can use Angular's HttpClient to make the API request
    }
  }
}
<form [formGroup]="scheduleForm" (ngSubmit)="submitSchedule()">
  <label for="owner">Owner:</label>
  <input type="text" id="owner" formControlName="owner" />

  <label for="day">Day:</label>
  <input type="text" id="day" formControlName="day" />

  <div formArrayName="courses">
    <div *ngFor="let course of scheduleForm.controls; let i = index; let l = last" [formGroupName]="i">
      <label for="time">Time:</label>
      <input type="text" id="time" formControlName="time" />

      <label for="course">Course:</label>
      <input type="text" id="course" formControlName="course" />

      <button type="button" (click)="removeCourse(i)">Remove Course</button>
    </div>
  </div>

  <button type="button" (click)="addCourse()">Add Course</button>

  <button type="submit">Submit</button>
  <pre>{{ scheduleForm.value | json }}</pre>
</form>
