import { Directive } from '@angular/core';

@Directive({
  selector: '[ShakeOnHover]'
})
export class ShakeOnHoverDirective {

  constructor(private elementRef: ElementRef, private renderer: Renderer2) { }

  @HostListener('mouseenter')
  onMouseEnter() {
    this.addShakeAnimation();
  }

  @HostListener('mouseleave')
  onMouseLeave() {
    this.removeShakeAnimation();
  }

  private addShakeAnimation() {
    this.renderer.addClass(this.elementRef.nativeElement, 'shake-on-hover');
  }

  private removeShakeAnimation() {
    this.renderer.removeClass(this.elementRef.nativeElement, 'shake-on-hover');
  }

}
