import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent implements OnInit {
  @Input() filterText: string = '';

  public items$: any;
  constructor(private service: DataService) {
  }

  ngOnInit() {
    this.getAll();
  }
  getAll(){
    this.service.getAll().subscribe(response => {
      this.items$ = response;
    });
  }

}
<div class="col justify-content-between main-page" style="width: 90%; padding-bottom: 5%;padding-top: 2%;padding-left: 5%">
  <div class=" item-container" *ngFor="let item of items$ | filter:filterText">
    <blog-item class="card  item" style="width: 18rem; height: 25rem; "[class.fade-in]="true" [image]="item.image" [title]="item.title" [price]="item.price" [description]="item.description" [seller]="item.seller" [id]="item.id">
    </blog-item>
  </div>
</div>

<div class="container main-page">
  <div class="row">
    <div class="col-md-4" *ngFor="let item of items$ | slice: offset: offset + pageSize">
      <div class="card mb-4">
        <img [src]="item.image" alt="Product Image" class="card-img-top" />
        <div class="card-body">
          <h5 class="card-title">{{ item.title }}</h5>
          <p class="card-text">Price: {{ item.price | currency }}</p>
          <button class="btn btn-primary">Check Out</button>
        </div>
      </div>
    </div>
  </div>
  <div class="row justify-content-center mt-4">
    <nav>
      <ul class="pagination">
        <li class="page-item" [class.disabled]="offset === 0">
          <a class="page-link" href="#" (click)="previousPage()">Previous</a>
        </li>
        <li class="page-item" [class.disabled]="offset >= items.length - pageSize">
          <a class="page-link" href="#" (click)="nextPage()">Next</a>
        </li>
      </ul>
    </nav>
  </div>
</div>
