import {Component, Input, OnInit} from '@angular/core';
import {style} from "@angular/animations";

@Component({
  selector: 'blog-item-text',
  templateUrl: './blog-item-text.component.html',
  styleUrls: ['./blog-item-text.component.css']
})
export class BlogItemTextComponent  implements OnInit {

  @Input() text?: string;
  @Input() price?: string;
  @Input() id?: number;


  constructor() { }

  ngOnInit(): void {
  }

  protected readonly style = style;
  protected readonly Number = Number;
}
<div class="card-body">
  <h5 class="card-title">{{ text }}</h5>
  <p class="card-text" style="color: black">Price: {{ Number(price) | currency }}</p>
  <button class="btn btn-primary" [routerLink]="['/blog/detail/' + id]">Check Out</button>
</div>
