import { Component } from '@angular/core';

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.css']
})
export class TeamsComponent {
  teams: { name: string }[] = [];
  newTeamName: string = '';

  createTeam() {
    if (this.newTeamName.trim() !== '') {
      this.teams.push({ name: this.newTeamName });
      this.newTeamName = '';
    }
  }
  ngOnInit() {
    console.log(this.teams);
    // Fetch or set the data for teams and assign it to this.teams
    // Example: this.teams = someFunctionToGetTeamsData();
  }
  deleteTeam(team: { name: string }) {
    const index = this.teams.indexOf(team);
    if (index !== -1) {
      this.teams.splice(index, 1);
    }
  }
}
<form (submit)="createTeam()">
  <input type="text" name="teamName" [(ngModel)]="newTeamName" placeholder="Enter team name" required>
  <button type="submit">Create Team</button>
</form>

<!-- Display a list of existing teams -->
<ul>
  <li *ngFor="let team of teams">{{ team.name }} <button (click)="deleteTeam(team)">Delete</button></li>
</ul>
