import { Directive } from '@angular/core';

@Directive({
  selector: '[ButtonHoverAnimation]'
})
export class ButtonHoverAnimationDirective {

  constructor(private elementRef: ElementRef, private renderer: Renderer2) { }

  @HostListener('mouseenter')
  onMouseEnter() {
    this.renderer.addClass(this.elementRef.nativeElement, 'hover-animation');
  }

  @HostListener('mouseleave')
  onMouseLeave() {
    this.renderer.removeClass(this.elementRef.nativeElement, 'hover-animation');
  }
}
