import {Component, Input, OnInit} from '@angular/core';
import {style} from "@angular/animations";

@Component({
  selector: 'blog-item-text',
  templateUrl: './blog-item-text.component.html',
  styleUrls: ['./blog-item-text.component.css']
})
export class BlogItemTextComponent  implements OnInit {

  @Input() text?: string;
  @Input() price?: string;
  @Input() id?: number;
  buttonText: string = 'check out';

  constructor() { }

  ngOnInit(): void {
  }

  protected readonly style = style;
}
<p>{{ text }} </p>
<p>{{price}}</p>
<a href="#" class="btn btn-primary" [routerLink]="['/blog/detail/' + id]" [style.color]="'red'|spin" ShakeOnHover>
  {{buttonText }}
</a>
