import { Component } from '@angular/core';
import {AuthService} from "../../services/auth.service";
import {Router} from "@angular/router";
import {DataService} from "../../services/data.service";

@Component({
  selector: 'post-add',
  templateUrl: './post-add.component.html',
  styleUrls: ['./post-add.component.css']
})
export class AddPostComponent {
  public credentials = {
    image: '',
    title: '',
    description: '',
    price: '',
  };

  constructor(private authService: AuthService,private dataService: DataService, public router: Router) {
  }

  ngOnInit() {
  }

  addPost() {
    if(this.authService.isLoggedIn()) {
      this.dataService.createPost(this.credentials).subscribe((result) => {
        return result;
      });
      this.router.navigate(['/']);
    }
  }

}
<section class="signup container">
  <h1 class="entry-title"><span>Add post</span></h1>
  <hr>
  <form class="form-horizontal" method="post" name="signup" id="signup">
    <div class="form-group">
      <label class="control-label col-sm-3">Title<span class="text-danger">*</span></label>
      <div class="col-md-5 col-sm-8">
        <div class="input-group">
          <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="text" class="form-control" name="text" id="title"
                 placeholder="Enter Title (5-25 char)" value="" [(ngModel)]="credentials.title">
        </div>
      </div>
    </div>
    <div class="form-group">
      <label class="control-label col-sm-3">image<span class="text-danger">*</span></label>
      <div class="col-md-8 col-sm-9">
        <div class="input-group">
          <span class="input-group-addon"><i class="glyphicon glyphicon-envelope"></i></span>
          <input type="text" class="form-control" name="image" id="image" placeholder="image"
                 value="" [(ngModel)]="credentials.image">
        </div>
      </div>
    </div>
    <div class="form-group">
      <label class="control-label col-sm-3">Description<span class="text-danger">*</span></label>
      <div class="col-md-5 col-sm-8">
        <div class="input-group">
          <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="text" class="form-control" name="text" id="text"
                 placeholder="description)" value="" [(ngModel)]="credentials.description">
        </div>
      </div>
    </div>
    <div class="form-group">
      <label class="control-label col-sm-3">Price<span class="text-danger">*</span></label>
      <div class="col-md-5 col-sm-8">
        <div class="input-group">
          <span class="input-group-addon"><i class="glyphicon glyphicon-lock"></i></span>
          <input type="number" step="0.01" class="form-control" name="price" id="price"
                 placeholder="10.20" value="" [(ngModel)]="credentials.price">
        </div>
      </div>
    </div>
    <div class="form-group">
      <div class="col-xs-offset-3 col-xs-10">
        <button (click)="addPost()" name="Submit" type="submit" class="btn btn-primary">Add post
        </button>
      </div>
    </div>
  </form>
</section>
