import {Component, Input, OnInit} from '@angular/core';
import {style} from "@angular/animations";

@Component({
  selector: 'Msite-item-text',
  templateUrl: './Msite-item-text.component.html',
  styleUrls: ['./Msite-item-text.component.css']
})
export class MsiteItemTextComponent  implements OnInit {

  @Input() text?: string;
  @Input() price?: string;
  @Input() id?: number;
  buttonText: string = 'check out';

  constructor() { }

  ngOnInit(): void {
  }

  protected readonly style = style;
  protected readonly Number = Number;
}
<div class="card-body">
  <h5 class="card-title text">{{ text }}</h5>
  <p class="card-text">Price: {{ Number(price) | currency }}</p>
  <button class="btn btn-primary"  [style.color]="'black' | spin" [routerLink]="['/item/detail/' + id]" ButtonHoverAnimation>{{buttonText}}</button>
</div>
