import { Component, OnInit } from '@angular/core';
import { DataService} from "../../services/data.service";  // Import your DataService

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.css']
})
export class TeamsComponent implements OnInit {
  teams: any[] = [];
  newTeamData: any = {}; // Data for creating a new team
  editTeamData: any = {}; // Data for editing a team
  selectedTeamId: string | null = null; // Store the ID of the selected team for editing

  constructor(private dataService: DataService) {} // Inject DataService

  ngOnInit() {
    this.loadTeams();
  }

  loadTeams() {
    this.dataService.getAllTeams().subscribe((data: any) => {
      this.teams = data;
    });
  }

  createTeam() {
    this.dataService.createTeam(this.newTeamData).subscribe(() => {
      this.loadTeams(); // Reload the list of teams after creating one
      this.newTeamData = {}; // Clear the form
    });
  }

  editTeam(id: string) {
    this.selectedTeamId = id;
    this.dataService.getTeamById(id).subscribe((data: any) => {
      this.editTeamData = data;
    });
  }

  updateTeam() {
    if (this.selectedTeamId) {
      this.dataService.updateTeam(this.selectedTeamId, this.editTeamData).subscribe(() => {
        this.loadTeams(); // Reload the list of teams after updating one
        this.editTeamData = {}; // Clear the form
        this.selectedTeamId = null; // Reset the selected team
      });
    }
  }

  deleteTeam(id: string) {
    this.dataService.deleteTeam(id).subscribe(() => {
      this.loadTeams(); // Reload the list of teams after deleting one
    });
  }

  // Add more methods for adding/removing members or other functionality

  cancelEdit() {
    this.editTeamData = {}; // Clear the form
    this.selectedTeamId = null; // Reset the selected team
  }
}
<form (submit)="createTeam()">
  <input type="text" name="teamName" [(ngModel)]="newTeamName" placeholder="Enter team name" required>
  <button type="submit">Create Team</button>
</form>

<!-- Display a list of existing teams -->
<ul>
  <li *ngFor="let team of teams">{{ team.name }}  <a [routerLink]="['/teams', 'edit', team.id]">Edit</a></li>
</ul>
