<div  style="height: 88vh;min-width: 100vw" *ngIf="authService.isLoggedIn()">

  <div class="column" *ngFor="let course of formArr2.controls; let x = index;" >
    <p  >{{course.get('dayOfWeek')?.value}}</p>
        <div class="each2" id="2222" *ngFor="let cour of former(course).controls ; let i = index;" >
          <label for="cors">Course {{i+1}}:</label>
          <div id="cors" class="doc">
            <label for="timeFrom">Time:</label>

                <p id="timeFrom"  >{{cour.get('timeFrom')?.value}} - {{cour.get('timeTo')?.value}}</p>

            <label for="course">Name:</label>
            <p type="text" id="course" >{{cour.get('course')?.value}}</p>
          </div></div>
    </div>
  <apx-chart
    [series]="chartOptions.series"
    [chart]="chartOptions.chart"
    [fill]="chartOptions.fill"

    [plotOptions]="chartOptions.plotOptions"
    [xaxis]="chartOptions.xaxis"
  ></apx-chart>
</div>
<div  style="height: 88vh;min-width: 100vw" *ngIf="!authService.isLoggedIn()">

  <div class="column" >
   <p>create new  shedule</p>
  </div>

</div>
