import { Component , OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';

@Component({
  selector: 'app-schedule-form',
  templateUrl: './schedule-form.component.html',
  styleUrls: ['./schedule-form.component.css']
})
export class ScheduleFormComponent  implements OnInit {

  scheduleForm: FormGroup = this.fb.group({
    owner: ['', Validators.required],
    day: ['', Validators.required],
    courses: this.fb.array([this.createCourse()])
  });

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.scheduleForm = this.fb.group({
      owner: ['', Validators.required],
      day: ['', Validators.required],
      courses: this.fb.array([this.createCourse()])
    });
  }

  createCourse(): FormGroup {
    return this.fb.group({
      time: ['', Validators.required],
      course: ['', Validators.required]
    });
  }

  addCourse() {
    const courses = this.scheduleForm.get('courses') as FormArray;
    courses.push(this.createCourse());
  }

  onSubmit() {
    if (this.scheduleForm.valid) {
      // Handle the form submission here, e.g., send the data to the backend
      const formData = this.scheduleForm.value;
      // Send the formData to your backend API
    }
  }
}
