<form (ngSubmit)="onSubmit()">
  <div>
    <label for="owner">Owner ID:</label>
    <input type="text" id="owner" formControlName="owner" required>
  </div>
  <div>
    <label for="day">Day:</label>
    <input type="text" id="day" formControlName="day" required>
  </div>
  <div formArrayName="courses">
    <div *ngFor="let course of scheduleForm['controls']['courses']['controls']; let i = index">
      <div [formGroupName]="i">
        <label for="time">Time:</label>
        <input type="text" id="time" formControlName="time" required>
        <label for="course">Course:</label>
        <input type="text" id="course" formControlName="course" required>
      </div>
    </div>


  </div>
  <button type="button" (click)="addCourse()">Add Course</button>
  <button type="submit">Add Schedule Entry</button>
</form>
