import {Component, Input, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, FormArray, Validators, AbstractControl} from '@angular/forms';
import { ValidatorFn } from '@angular/forms';
import {AuthService} from "../../services/auth.service";

function timeRangeValidator(): ValidatorFn {
  return (control: AbstractControl): { [key: string]: any } | null => {
    const timeRange = control.value;
    const min = '7';
    const max = '21';
    if (!timeRange) {
      return null; // Empty value, no validation needed
    }

    // Regular expression to match the time range format (hh:mm-hh:mm)
    const timeRangePattern = /^\d{2}:\d{2}-\d{2}:\d{2}$/;

    if (!timeRange.match(timeRangePattern)) {
      return { invalidTimeRange: true }; // Invalid format
    }

    // Split the time range into start and end times
    const [startTime, endTime] = timeRange.split('-');
    const [startHour, startMinute] = startTime.split(':');
    const [endHour, endMinute] = endTime.split(':');

    // Convert the time components to numbers
    const startHourNum = Number(startHour);
    const startMinuteNum = Number(startMinute);
    const endHourNum = Number(endHour);
    const endMinuteNum = Number(endMinute);

    // Check if the time components are valid
    if (
      isNaN(startHourNum) || isNaN(startMinuteNum) ||
      isNaN(endHourNum) || isNaN(endMinuteNum) ||
      startHourNum < 7 || startHourNum > 21 || startMinuteNum < 0 || startMinuteNum > 59 ||
      endHourNum < startHourNum || endHourNum > 21 || endMinuteNum < startMinuteNum || endMinuteNum > 59
    ) {
      return { invalidTimeRange: true }; // Invalid time components
    }

    // Check if the start time is earlier than the end time
    if (startHourNum > endHourNum || (startHourNum === endHourNum && startMinuteNum >= endMinuteNum)) {
      return { invalidTimeRange: true }; // Start time is later or equal to end time
    }

    return null; // Valid time range
  };
}

@Component({
  selector: 'app-schedule-form',
  templateUrl: './schedule-form.component.html',
  styleUrls: ['./schedule-form.component.css']
})
export class ScheduleFormComponent  implements OnInit {

  scheduleForm!: FormGroup;
  weekdays: string[] = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  formatTimeRange(timeF: HTMLInputElement,timeT: HTMLInputElement,Course: AbstractControl): string {
    const timeFrom = timeF.value;
    const timeTo = timeT.value;
    //Course.get('time')?.setValue((timeFrom+"-"+timeTo).toString());
    return `${timeFrom} - ${timeTo}`;
  }
  constructor(public authService: AuthService,private formBuilder: FormBuilder) {
    this.createContactForm();
  }
  createContactForm() {
    this.scheduleForm = this.formBuilder.group({
      owner: ['', Validators.required],
      scheduleEntries: this.formBuilder.array([this.createScheduleEntryFormGroup()])
    });
    this.scheduleForm.get('owner')?.setValue(this.authService.currentUser.userId);
  }
  createScheduleEntryFormGroup(): FormGroup {
    return this.formBuilder.group({
      dayOfWeek: ['', Validators.required],
      courses: this.formBuilder.array([this.createCourseFormGroup()])
    });
  }
  createCourseFormGroup(): FormGroup {
    return this.formBuilder.group({
      time: ['',[ Validators.required,timeRangeValidator()]],
      course: ['',[ Validators.required,Validators.minLength(2)]]
    });
  }
  get formArr() {
    return this.formArr2.at(0).get('courses') as FormArray;
  }

  get formArr2() {
    return this.scheduleForm.get('scheduleEntries') as FormArray;
  }
  addRow({obj}: { obj: any }) {
    return this.formBuilder.group({
      time: [obj.time],
      course: [obj.course],
    });
  }
  addNewRow() {
    let obj1 = {
      time: '',
      course: '',
    };
    this.formArr.push(this.addRow({obj: obj1}));
  }

  ngOnInit(): void {
  }


  addCourse(): void {
    const coursesArray = this.scheduleForm.get('courses') as FormArray;
    coursesArray.push(this.createCourseFormGroup());
  }

  removeCourse(index: number): void {
    const coursesArray = this.formArr as FormArray;
    coursesArray.removeAt(index);
  }

  submitSchedule(): void {
    if (this.scheduleForm.valid) {
      console.log("ok");      // Send the form data to your backend API for saving to the database
      // You can use Angular's HttpClient to make the API request
    }else{
      console.log("!ok");
    }
  }
}
<form [formGroup]="scheduleForm" (ngSubmit)="submitSchedule()">
  <tbody formArrayName="scheduleEntries">
  <tr *ngFor="let course of formArr2.controls; let x = index;" [formGroupName]="x">
    <td>
      <label for="dayOfWeek">dayOfWeek:</label>
      <select id="dayOfWeek" formControlName="dayOfWeek">
        <option *ngFor="let weekday of weekdays" [value]="weekday">{{ weekday }}</option>
      </select>
    </td>
    <td formArrayName="courses">
      <label for="2222">dayOfWeek:</label>
        <div  id="2222" *ngFor="let course of formArr.controls; let i = index;" [formGroupName]="i">

            <label for="times">Time:</label>
          <div id="times">
            <label for="timefrom">From:</label>
            <input type="time" id="timefrom" #timefromInput (input)="formatTimeRange(timefromInput,timetoInput,course);" min="07:00" max="21:00" required />
            <label for="timeto">To:</label>
            <input type="time" id="timeto" #timetoInput (input)="formatTimeRange(timefromInput,timetoInput,course); " min="{{timefromInput.value}}" max="21:00" required />


          </div>

          <div>Time Range: <p id="time" formControlName="time">{{ formatTimeRange(timefromInput,timetoInput,course) }}</p></div>
            <label for="course">Course:</label>
            <input type="text" id="course" formControlName="course" />

          <button type="button" (click)="removeCourse(i)">Remove Course 1</button>
        </div>
    </td>
    <button type="button" (click)="addNewRow()" class="btn btn-dark">add Course 1</button>
    <button type="button" (click)="removeCourse(x)">Remove Course 2</button>
  </tr>
  </tbody>

  <button type="submit">Submit</button>
  <pre>{{ scheduleForm.value | json }}</pre>
  <pre>{{ scheduleForm.valid }}</pre>
</form>
