import { Component, OnInit } from '@angular/core';
import {DataService} from "../services/data.service";
import { ActivatedRoute } from '@angular/router';
import {AuthService} from "../services/auth.service";

@Component({
  selector: '-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent implements OnInit {
  public credentials = {
    image: '',
    title: '',
    description: '',
    price: '',
    seller:this.authService.currentUser.name,


  };// Object to hold the form data
  productId: string = ''; // Property to store the product ID

  constructor(private authService: AuthService,private productService: DataService, private route: ActivatedRoute) { }

  ngOnInit(): void {
    // Get the product ID from the route parameters
    this.productId = this.route.snapshot.params['id'];

  }

  updateProduct(): void {
    this.productService
  }
}
