import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent implements OnInit {
  @Input() filterText: string = '';

  public items$: any;
  constructor(private service: DataService) {
  }

  ngOnInit() {
    this.getAll();
  }
  getAll(){
    this.service.getAll().subscribe(response => {
      this.items$ = response;
    });
  }

}
<div class="row jus">
  <div class="col-md-2 col-sm-12 mb-4 m-4" *ngFor="let item of items$ | filter:filterText">
    <blog-item class="card p-3" style="width: 18rem;" [image]="item.image" [title]="item.title" [price]="item.price" [description]="item.description" [seller]="item.seller" [id]="item.id">
    </blog-item>
  </div>
</div>
